clear all;close all;S = 50;       % Valeur du sous-jacentK = 50;       % Striker = 0.05;     % Taux d''intérêt sans risquesigma = 0.2; % Volatiliédelta = 0.02; % Taux de dividendeT = 3; % MaturitéM = 80; % Nombre de points de maillage d'actifsN = 200; % Nombre de points de maillage en tempsSzero = 0; % Valeur extremeSmax= 150; % Valeur extremeomega=1.2;tol=0.001; % Parametres SORsolution_mesh=zeros(N+1,M+1); % Tableau de la solution approchéeSmesh=0:(Smax/M):Smax; % maillage en STmesh=T:-(T/N):0; % maillage en temps tdt=T/N; % pas de temps, dtdS=Smax/M; % pas d'actifs, dSsolution_mesh(1,:)= max(Smesh-K,0); % Payoff (Call)solution_mesh(:,1)= 0; % Condition à la limite en S=0solution_mesh(:,M+1)= Smax; % Condition à la limite en S=M% Definition des fonctions A, B ,C & Fa = @(i) dt*(0.25*(i*sigma)^2 - 0.5*i*(r-delta));b = @(i) -1 -dt* (0.5*(i*sigma)^2+r); c = @(i) dt*(0.25*(sigma*i)^2+0.5*i*(r-delta));